Shader.Vertex:
attribute vec2 vPosition;
uniform mat4 uMatViewProjection;
uniform mat4 uMatModel;

void main()
{
   gl_Position = (uMatViewProjection * uMatModel) * vec4(vPosition, 0.0, 1.0);
}

Shader.Fragment:
precision mediump float;
varying vec2 coord;
uniform sampler2D uSamp;
uniform vec4 uColor;

void main()
{
   gl_FragColor = uColor;
}
